// Parse Synology Alert
const text = $input.first().json.body?.text || $input.first().json.text || '';
const receivedAt = new Date().toISOString();

let hostname = 'unknown';
let date = '';
let time = '';
let eventMessage = text;
let format = 'unknown';

// Check if structured format (contains 'A new system event')
if (text.includes('A new system event')) {
  format = 'structured';
  const lines = text.split('\n');
  const headerMatch = lines[0].match(/on your ([\w-]+) on (\d{2}-\d{2}-\d{4}) at (\d{2}:\d{2})/);
  if (headerMatch) {
    hostname = headerMatch[1];
    date = headerMatch[2];
    time = headerMatch[3];
  }
  eventMessage = lines[1] || '';
} else {
  // Simple format: [hostname] message
  format = 'simple';
  const simpleMatch = text.match(/\[([^\]]+)\] (.+)/);
  if (simpleMatch) {
    hostname = simpleMatch[1];
    eventMessage = simpleMatch[2];
  }
}

// Determine alert type
let alertType = 'unknown';
let category = 'Unknown';
let severity = 'Info';
let level = 'INFO';

// Backup category
if (eventMessage.match(/backup task.*completed successfully/i)) {
  alertType = 'backup_completed';
  category = 'Backup';
  severity = 'Info';
  level = 'INFO';
} else if (eventMessage.match(/backup task.*partially completed/i)) {
  alertType = 'backup_partial';
  category = 'Backup';
  severity = 'Warning';
  level = 'WARN';
} else if (eventMessage.match(/backup task.*failed/i)) {
  alertType = 'backup_failed';
  category = 'Backup';
  severity = 'Critical';
  level = 'ERROR';
} else if (eventMessage.match(/Backup task for .* completed/i)) {
  alertType = 'active_backup_completed';
  category = 'Backup';
  severity = 'Info';
  level = 'INFO';
} else if (eventMessage.match(/Backup task for .* failed/i)) {
  alertType = 'active_backup_failed';
  category = 'Backup';
  severity = 'Critical';
  level = 'ERROR';
} else if (eventMessage.match(/Device .* is offline/i)) {
  alertType = 'device_offline';
  category = 'Backup';
  severity = 'Warning';
  level = 'WARN';
} else if (eventMessage.match(/replication task.*failed/i)) {
  alertType = 'replication_failed';
  category = 'Backup';
  severity = 'Critical';
  level = 'ERROR';
}
// CMS category
else if (eventMessage.match(/managed server.*is offline/i)) {
  alertType = 'server_offline';
  category = 'CMS';
  severity = 'Critical';
  level = 'ERROR';
} else if (eventMessage.match(/managed server.*is online/i)) {
  alertType = 'server_online';
  category = 'CMS';
  severity = 'Info';
  level = 'INFO';
} else if (eventMessage.match(/unable to connect to the CMS/i)) {
  alertType = 'cms_connection_failure';
  category = 'CMS';
  severity = 'Critical';
  level = 'ERROR';
} else if (eventMessage.match(/reconnected to the CMS/i)) {
  alertType = 'cms_connection_restored';
  category = 'CMS';
  severity = 'Info';
  level = 'INFO';
}
// Storage category
else if (eventMessage.match(/Volume.*degraded/i)) {
  alertType = 'volume_degraded';
  category = 'Storage';
  severity = 'Critical';
  level = 'ERROR';
} else if (eventMessage.match(/Volume.*crashed/i)) {
  alertType = 'volume_crashed';
  category = 'Storage';
  severity = 'Critical';
  level = 'ERROR';
} else if (eventMessage.match(/Disk.*failing/i)) {
  alertType = 'disk_failing';
  category = 'Storage';
  severity = 'Critical';
  level = 'ERROR';
} else if (eventMessage.match(/Bad sectors/i)) {
  alertType = 'disk_bad_sectors';
  category = 'Storage';
  severity = 'Warning';
  level = 'WARN';
} else if (eventMessage.match(/almost full|less than 1%/i)) {
  alertType = 'storage_space_critical';
  category = 'Storage';
  severity = 'Critical';
  level = 'ERROR';
} else if (eventMessage.match(/\d+% free space/i)) {
  alertType = 'storage_space_low';
  category = 'Storage';
  severity = 'Warning';
  level = 'WARN';
}
// Hardware category
else if (eventMessage.match(/Power failure/i)) {
  alertType = 'ups_power_failure';
  category = 'Hardware';
  severity = 'Critical';
  level = 'ERROR';
} else if (eventMessage.match(/UPS battery is low/i)) {
  alertType = 'ups_low_battery';
  category = 'Hardware';
  severity = 'Critical';
  level = 'ERROR';
} else if (eventMessage.match(/temperature.*high/i)) {
  alertType = 'temperature_warning';
  category = 'Hardware';
  severity = 'Warning';
  level = 'WARN';
} else if (eventMessage.match(/Fan.*failed/i)) {
  alertType = 'fan_failure';
  category = 'Hardware';
  severity = 'Critical';
  level = 'ERROR';
}
// Security category
else if (eventMessage.match(/Failed login attempt/i)) {
  alertType = 'login_failed';
  category = 'Security';
  severity = 'Warning';
  level = 'WARN';
} else if (eventMessage.match(/IP address.*blocked/i)) {
  alertType = 'ip_blocked';
  category = 'Security';
  severity = 'Warning';
  level = 'WARN';
}
// System category
else if (eventMessage.match(/Test Message/i)) {
  alertType = 'test_message';
  category = 'System';
  severity = 'Info';
  level = 'INFO';
} else if (eventMessage.match(/DSM.*available/i)) {
  alertType = 'dsm_update_available';
  category = 'System';
  severity = 'Info';
  level = 'INFO';
} else if (eventMessage.match(/Update available for/i)) {
  alertType = 'package_update_available';
  category = 'System';
  severity = 'Info';
  level = 'INFO';
} else if (eventMessage.match(/System has started/i)) {
  alertType = 'system_boot';
  category = 'System';
  severity = 'Info';
  level = 'INFO';
} else if (eventMessage.match(/System is shutting down/i)) {
  alertType = 'system_shutdown';
  category = 'System';
  severity = 'Info';
  level = 'INFO';
}

// Extract additional data based on type
let extractedData = {};

if (alertType === 'server_offline' || alertType === 'server_online') {
  const ipMatch = eventMessage.match(/\(([0-9.]+)\)/);
  if (ipMatch) extractedData.serverIp = ipMatch[1];
}

if (alertType === 'cms_connection_failure' || alertType === 'cms_connection_restored') {
  const ipMatch = eventMessage.match(/\(([0-9.]+)\)/);
  if (ipMatch) extractedData.cmsHostIp = ipMatch[1];
}

if (alertType === 'login_failed') {
  const match = eventMessage.match(/from ([0-9.]+) for user (\w+)/);
  if (match) {
    extractedData.sourceIp = match[1];
    extractedData.username = match[2];
  }
}

if (alertType === 'ip_blocked') {
  const match = eventMessage.match(/IP address ([0-9.]+)/);
  if (match) extractedData.blockedIp = match[1];
}

return {
  json: {
    // Original data
    rawText: text,
    receivedAt,

    // Parsed metadata
    hostname,
    date,
    time,
    format,

    // Alert classification
    alertType,
    category,
    severity,
    level,

    // Message content
    eventMessage,

    // Extracted fields
    ...extractedData
  }
};
